import { fetchGraphQL } from '@/lib/graphql';
import { stripScripts, stripUnwantedTerms } from '@/lib/sanitize';
import { Sparkles, CheckCircle2, Award, Users, Globe } from 'lucide-react';
import Image from 'next/image';
import { fetchRankMathSEO, mapRankMathToMetadata } from '@/lib/seo';
import { Metadata } from 'next';

export async function generateMetadata(): Promise<Metadata> {
    const seo = await fetchRankMathSEO('https://bootflare.com/about-us/');
    if (seo) return mapRankMathToMetadata(seo);
    return { title: 'About Us | Bootflare' };
}


const GET_ABOUT_PAGE = `
  query GetAboutPage {
    page(id: "about-us", idType: URI) {
      title
      content
      featuredImage {
        node {
          sourceUrl
        }
      }
    }
  }
`;

interface Page {
    title: string;
    content: string;
    featuredImage?: {
        node: {
            sourceUrl: string;
        }
    }
}


export default async function AboutPage() {
    let page: Page | null = null;
    try {
        const data: { page: Page } = await fetchGraphQL(GET_ABOUT_PAGE);
        page = data.page;
    } catch (error) {
        console.error('Error fetching about page:', error);
    }

    if (!page) {
        return (
            <div className="bg-slate-50 min-h-screen pt-32 pb-20">
                <div className="container text-center max-w-2xl px-6">
                    <h1 className="text-4xl md:text-6xl font-black text-slate-900 mb-8">About <span className="text-gradient">Bootflare</span></h1>
                    <p className="text-lg text-slate-500 font-light leading-relaxed mb-12">
                        We are a elite digital agency powering online presence for forward-thinking businesses through customer-focused design and innovation.
                    </p>
                </div>
            </div>
        );
    }

    const sanitizedContent = stripUnwantedTerms(stripScripts(page.content));

    return (
        <div className="bg-white min-h-screen pb-32" suppressHydrationWarning>
            {/* Hero Section */}
            <section className="relative pt-32 pb-20 overflow-hidden bg-slate-50">
                <div className="absolute top-0 right-0 w-1/2 h-1/2 bg-primary/5 rounded-full blur-[120px] -z-0" />
                <div className="container relative z-10 px-6">
                    <div className="text-center max-w-3xl mx-auto">
                        <div className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-primary/10 text-primary font-bold text-xs mb-8 uppercase tracking-widest">
                            <Sparkles className="w-3 h-3" /> Our Mission
                        </div>
                        <h1 className="text-4xl md:text-7xl font-black text-slate-900 mb-8 leading-tight font-ubuntu">
                            {page.title}
                        </h1>
                        <p className="text-xl text-slate-500 font-light leading-relaxed italic border-l-4 border-primary pl-8 text-left max-w-2xl mx-auto">
                            "Empowering businesses to adapt and flourish in the digital era through creative excellence and technical innovation."
                        </p>
                    </div>
                </div>
            </section>

            <div className="container px-6 -mt-10 relative z-20">
                {page.featuredImage && (
                    <div className="rounded-[3rem] overflow-hidden shadow-2xl border-8 border-white ring-1 ring-slate-100 mb-20 aspect-video md:aspect-[21/9]">
                        <img src={page.featuredImage.node.sourceUrl} alt={page.title} className="w-full h-full object-cover" />
                    </div>
                )}

                <div className="grid lg:grid-cols-3 gap-16 items-start">
                    {/* Main Content */}
                    <div className="lg:col-span-2">
                        <div
                            className="prose prose-slate prose-2xl max-w-none text-slate-700 leading-relaxed font-light article-content about-content
                                [&_p]:mb-10 [&_p:last-child]:mb-0
                                [&_ul]:list-disc [&_ul]:pl-6 [&_ul]:mb-8 [&_ul]:space-y-3
                                [&_ol]:list-decimal [&_ol]:pl-6 [&_ol]:mb-8 [&_ol]:space-y-3
                                [&_li]:mb-2
                                [&_h2]:text-3xl [&_h2]:font-bold [&_h2]:mt-16 [&_h2]:mb-8 [&_h2]:text-slate-900
                                [&_h3]:text-2xl [&_h3]:font-bold [&_h3]:mt-12 [&_h3]:mb-6 [&_h3]:text-slate-900"
                            dangerouslySetInnerHTML={{ __html: sanitizedContent }}
                            suppressHydrationWarning
                        />
                    </div>

                    {/* Sidebar Stats/Trust */}
                    <div className="space-y-8 sticky top-32">
                        <div className="card-premium !bg-primary text-white !p-10">
                            <h3 className="text-2xl font-bold mb-6">Why Bootflare?</h3>
                            <ul className="space-y-4 text-primary-hover/90">
                                <li className="flex items-start gap-3">
                                    <CheckCircle2 className="w-5 h-5 shrink-0" />
                                    <span className="font-medium">Data-Driven Strategy</span>
                                </li>
                                <li className="flex items-start gap-3">
                                    <CheckCircle2 className="w-5 h-5 shrink-0" />
                                    <span className="font-medium">User-Centric Design</span>
                                </li>
                                <li className="flex items-start gap-3">
                                    <CheckCircle2 className="w-5 h-5 shrink-0" />
                                    <span className="font-medium">Performance Optimized</span>
                                </li>
                                <li className="flex items-start gap-3">
                                    <CheckCircle2 className="w-5 h-5 shrink-0" />
                                    <span className="font-medium">Scalable Solutions</span>
                                </li>
                            </ul>
                        </div>

                        <div className="grid grid-cols-2 gap-4">
                            <div className="p-6 bg-slate-50 rounded-[2rem] text-center border border-slate-100">
                                <Users className="w-8 h-8 text-primary mx-auto mb-4" />
                                <p className="text-2xl font-black text-slate-900">2k+</p>
                                <p className="text-xs font-bold text-slate-400 uppercase">Clients</p>
                            </div>
                            <div className="p-6 bg-slate-50 rounded-[2rem] text-center border border-slate-100">
                                <Award className="w-8 h-8 text-primary mx-auto mb-4" />
                                <p className="text-2xl font-black text-slate-900">15+</p>
                                <p className="text-xs font-bold text-slate-400 uppercase">Awards</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

import { fetchGraphQL } from '@/lib/graphql';
import { stripScripts } from '@/lib/sanitize';
import { Calendar, User, ArrowLeft, Share2, Clock } from 'lucide-react';
import Link from 'next/link';
import { Metadata } from 'next';
import { mapWPToMetadata } from '@/lib/seo';

const GET_SINGLE_POST = `
  query GetSinglePost($slug: ID!) {
    post(id: $slug, idType: SLUG) {
      title
      content
      excerpt
      date
      featuredImage {
        node {
          sourceUrl
        }
      }
      author {
        node {
          name
        }
      }
      categories {
        nodes {
          name
          slug
        }
      }
    }
  }
`;

export async function generateMetadata({ params }: { params: Promise<{ slug: string }> }): Promise<Metadata> {
  const { slug } = await params;
  try {
    const data = await fetchGraphQL<{ post: Post }>(GET_SINGLE_POST, { slug });
    if (data.post) {
      return mapWPToMetadata(data.post, 'Blog - Bootflare');
    }
  } catch (error) {
    console.error('Error generating metadata for blog post:', error);
  }
  return { title: 'Post Not Found | Bootflare' };
}

interface Post {
  title: string;
  content: string;
  excerpt?: string;
  date: string;
  featuredImage?: {
    node: {
      sourceUrl: string;
    }
  };
  author?: {
    node: {
      name: string;
    }
  };
  categories: {
    nodes: {
      name: string;
      slug: string;
    }[]
  };
}

export async function generateStaticParams() {
  const GET_ALL_POST_SLUGS = `
    query GetAllPostSlugs {
      posts(first: 100) {
        nodes {
          slug
        }
      }
    }
  `;
  try {
    const data: { posts: { nodes: { slug: string }[] } } = await fetchGraphQL(GET_ALL_POST_SLUGS);
    return data.posts.nodes.map((post) => ({
      slug: post.slug,
    }));
  } catch (error) {
    console.error('Error generating static params for posts:', error);
    return [];
  }
}

export default async function BlogPost({ params }: { params: Promise<{ slug: string }> }) {
  const { slug } = await params;
  let post: Post | null = null;
  try {
    const data: { post: Post } = await fetchGraphQL(GET_SINGLE_POST, { slug });
    post = data.post;
  } catch (error) {
    console.error('Error fetching post:', error);
  }

  if (!post) {
    return <div className="container py-32 text-center text-slate-500">Post not found</div>;
  }

  const sanitizedContent = stripScripts(post.content);

  return (
    <article className="bg-white min-h-screen pb-20" suppressHydrationWarning>


      <div className="container pt-20 pb-16">
        <Link href="/blog" className="inline-flex items-center gap-2 text-slate-500 hover:text-primary transition-colors font-bold mb-12 group">
          <ArrowLeft className="w-4 h-4 group-hover:-translate-x-1 transition-transform" /> Back to Insights
        </Link>

        <div className="max-w-4xl mx-auto">
          {/* Meta */}
          <div className="flex flex-wrap items-center gap-4 mb-8">
            {post.categories.nodes.map(cat => (
              <Link key={cat.slug} href={`/category/${cat.slug}`} className="text-[10px] font-bold text-primary uppercase tracking-widest px-3 py-1 bg-primary/10 rounded-full hover:bg-primary hover:text-white transition-all">
                {cat.name}
              </Link>
            ))}
            <div className="flex items-center gap-2 text-slate-400 text-xs font-bold uppercase tracking-wider">
              <Calendar className="w-3 h-3" />
              {new Date(post.date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' })}
            </div>
            <div className="flex items-center gap-2 text-slate-400 text-xs font-bold uppercase tracking-wider">
              <Clock className="w-3 h-3" />
              <span>5 min read</span>
            </div>
          </div>

          <h1 className="text-4xl md:text-6xl font-bold mb-12 leading-tight text-slate-900 font-ubuntu">
            {post.title}
          </h1>

          {post.featuredImage && (
            <div className="mb-20 rounded-[3rem] overflow-hidden shadow-2xl shadow-slate-200 border-8 border-white ring-1 ring-slate-100">
              <img src={post.featuredImage.node.sourceUrl} alt={post.title} className="w-full h-auto" />
            </div>
          )}

          <div className="flex flex-col lg:flex-row gap-16">
            {/* Content Side */}
            <div className="flex-1">
              <div
                className="prose prose-slate prose-xl max-w-none text-slate-700 leading-relaxed font-light article-content
                  [&_p]:mb-10 [&_p:last-child]:mb-0
                  [&_h2]:text-3xl [&_h2]:font-bold [&_h2]:mt-16 [&_h2]:mb-8 [&_h2]:text-slate-900
                  [&_h3]:text-2xl [&_h3]:font-bold [&_h3]:mt-12 [&_h3]:mb-6 [&_h3]:text-slate-900
                  [&_ul]:list-disc [&_ul]:pl-6 [&_ul]:mb-8 [&_ul]:space-y-3
                  [&_ol]:list-decimal [&_ol]:pl-6 [&_ol]:mb-8 [&_ol]:space-y-3
                  [&_li]:mb-2"
                dangerouslySetInnerHTML={{ __html: sanitizedContent }}
                suppressHydrationWarning
              />
            </div>

            {/* Author/Share Side */}
            <div className="lg:w-72 shrink-0">
              <div className="sticky top-32 space-y-12">
                {/* Author Card */}
                <div className="p-8 rounded-[2rem] bg-slate-50 border border-slate-100">
                  <h4 className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-6">Published By</h4>
                  <div className="flex items-center gap-4">
                    <div className="w-12 h-12 rounded-full bg-slate-200" />
                    <div>
                      <p className="font-bold text-slate-900">{post.author?.node?.name || "Andy"}</p>
                      <p className="text-xs text-slate-500">Editorial Team</p>
                    </div>
                  </div>
                </div>

                {/* Share */}
                <div>
                  <h4 className="text-[10px] font-bold text-slate-400 uppercase tracking-widest mb-6">Spread the knowledge</h4>
                  <div className="flex gap-3">
                    <button className="w-10 h-10 rounded-full bg-slate-100 flex items-center justify-center text-slate-600 hover:bg-primary hover:text-white transition-all">
                      <Share2 className="w-4 h-4" />
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </article>
  );
}

import { fetchGraphQL } from '@/lib/graphql';
import Link from 'next/link';
import { stripScripts } from '@/lib/sanitize';
import { Calendar, ChevronRight, Clock, User } from 'lucide-react';
import { fetchRankMathSEO, mapRankMathToMetadata } from '@/lib/seo';
import { Metadata } from 'next';

export async function generateMetadata(): Promise<Metadata> {
  const seo = await fetchRankMathSEO('https://bootflare.com/blog/');
  if (seo) return mapRankMathToMetadata(seo);
  return { title: 'Blog | Bootflare' };
}


const GET_POSTS = `
  query GetPosts($first: Int) {
    posts(first: $first) {
      nodes {
        id
        title
        slug
        excerpt
        date
        featuredImage {
          node {
            sourceUrl
          }
        }
        categories {
          nodes {
            name
            slug
          }
        }
      }
    }
  }
`;

interface Post {
  id: string;
  title: string;
  slug: string;
  excerpt: string;
  date: string;
  featuredImage?: {
    node: {
      sourceUrl: string;
    }
  };
  categories: {
    nodes: {
      name: string;
      slug: string;
    }[]
  };
}

export default async function BlogPage() {
  let posts: Post[] = [];
  try {
    const data: { posts: { nodes: Post[] } } = await fetchGraphQL(GET_POSTS, { first: 20 });
    posts = data.posts.nodes;
  } catch (error) {
    console.error('Error fetching posts:', error);
  }

  return (
    <div className="bg-slate-50 min-h-screen pt-32 pb-20">
      <div className="container">
        {/* Header */}
        <div className="text-center max-w-2xl mx-auto mb-16">
          <h1 className="text-4xl md:text-6xl font-bold text-slate-900 mb-6 font-ubuntu">Insights & <span className="text-gradient">Resources</span></h1>
          <p className="text-lg text-slate-500 font-light">
            Discover the latest trends in web design, SEO strategies, and digital transformation tips to scale your brand.
          </p>
        </div>

        {/* Featured Post (Optional, taking first) */}
        {posts.length > 0 && (
          <div className="mb-16">
            <div className="grid lg:grid-cols-2 gap-10 items-center bg-white rounded-[3rem] overflow-hidden shadow-sm border border-slate-100 p-6 md:p-10">
              {posts[0].featuredImage && (
                <Link href={`/blog/${posts[0].slug}`} className="block relative aspect-video rounded-[2rem] overflow-hidden group">
                  <img
                    src={posts[0].featuredImage.node.sourceUrl}
                    alt={posts[0].title}
                    className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-700"
                  />
                </Link>
              )}
              <div>
                <div className="flex gap-2 mb-6">
                  {posts[0].categories.nodes.map(cat => (
                    <Link key={cat.slug} href={`/category/${cat.slug}`} className="text-xs font-bold text-primary uppercase tracking-widest px-3 py-1 bg-primary/10 rounded-full hover:bg-primary hover:text-white transition-all">
                      {cat.name}
                    </Link>
                  ))}
                </div>
                <Link href={`/blog/${posts[0].slug}`}>
                  <h2 className="text-3xl md:text-4xl font-bold mb-6 hover:text-primary transition-colors text-slate-900">{posts[0].title}</h2>
                </Link>
                <div
                  className="text-slate-500 text-lg line-clamp-3 mb-8 font-light [&_p]:mb-0"
                  dangerouslySetInnerHTML={{ __html: stripScripts(posts[0].excerpt) }}
                  suppressHydrationWarning
                />
                <Link href={`/blog/${posts[0].slug}`} className="btn-premium group !px-10">
                  Read Article <ChevronRight className="w-5 h-5 ml-2 group-hover:translate-x-1 transition-transform" />
                </Link>
              </div>
            </div>
          </div>
        )}

        {/* Grid Section */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {posts.slice(1).map((post) => (
            <article key={post.id} className="card-premium !p-0 flex flex-col group h-full">
              {post.featuredImage && (
                <Link href={`/blog/${post.slug}`} className="relative h-64 overflow-hidden rounded-t-[2rem]">
                  <img
                    src={post.featuredImage.node.sourceUrl}
                    alt={post.title}
                    className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-700"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent" />
                </Link>
              )}
              <div className="p-8 flex flex-col flex-1">
                <div className="flex items-center gap-4 mb-4 text-[10px] font-bold text-slate-400 uppercase tracking-widest">
                  <Link href={`/category/${post.categories.nodes[0]?.slug}`} className="text-primary hover:underline">
                    {post.categories.nodes[0]?.name}
                  </Link>
                  <span>•</span>
                  <div className="flex items-center gap-1">
                    <Calendar className="w-3 h-3" />
                    {new Date(post.date).toLocaleDateString(undefined, { month: 'short', day: 'numeric', year: 'numeric' })}
                  </div>
                </div>

                <Link href={`/blog/${post.slug}`} className="mb-4">
                  <h3 className="text-xl font-bold text-slate-900 hover:text-primary transition-colors line-clamp-2 leading-tight">
                    {post.title}
                  </h3>
                </Link>

                <div
                  className="text-slate-500 text-sm line-clamp-2 mb-8 font-light flex-1 [&_p]:mb-0"
                  dangerouslySetInnerHTML={{ __html: stripScripts(post.excerpt) }}
                  suppressHydrationWarning
                />

                <Link href={`/blog/${post.slug}`} className="flex items-center gap-2 text-primary font-bold text-sm group-link">
                  Read Story <ChevronRight className="w-4 h-4 group-hover:translate-x-1 transition-transform" />
                </Link>
              </div>
            </article>
          ))}
        </div>

        {posts.length === 0 && (
          <div className="text-center py-32 bg-white rounded-[3rem] border border-dashed border-slate-200">
            <p className="text-slate-400 text-xl font-light">No articles found. Stay tuned for updates!</p>
          </div>
        )}
      </div>
    </div>
  );
}

import { fetchGraphQL } from '@/lib/graphql';
import Link from 'next/link';
import { stripScripts } from '@/lib/sanitize';
import { Calendar, ChevronRight, Hash } from 'lucide-react';
import { Metadata } from 'next';

const GET_CATEGORY_POSTS = `
  query GetCategoryPosts($slug: ID!) {
    category(id: $slug, idType: SLUG) {
      name
      description
      posts(first: 20) {
        nodes {
          id
          title
          slug
          excerpt
          date
          featuredImage {
            node {
              sourceUrl
            }
          }
          categories {
            nodes {
              name
              slug
            }
          }
        }
      }
    }
  }
`;

interface Post {
    id: string;
    title: string;
    slug: string;
    excerpt: string;
    date: string;
    featuredImage?: {
        node: {
            sourceUrl: string;
        }
    };
    categories: {
        nodes: {
            name: string;
            slug: string;
        }[]
    };
}

interface CategoryData {
    category: {
        name: string;
        description: string;
        posts: {
            nodes: Post[];
        };
    } | null;
}

export async function generateMetadata({ params }: { params: Promise<{ slug: string }> }): Promise<Metadata> {
    const { slug } = await params;
    try {
        const data = await fetchGraphQL<CategoryData>(GET_CATEGORY_POSTS, { slug });
        if (data.category) {
            return {
                title: `${data.category.name} | Blog Category | Bootflare`,
                description: data.category.description || `Browse all articles in the ${data.category.name} category.`
            };
        }
    } catch (error) {
        console.error('Error generating category metadata:', error);
    }
    return { title: 'Category Not Found | Bootflare' };
}

export async function generateStaticParams() {
    const GET_CATEGORY_SLUGS = `
    query GetCategorySlugs {
      categories(first: 100) {
        nodes {
          slug
        }
      }
    }
  `;
    try {
        const data: { categories: { nodes: { slug: string }[] } } = await fetchGraphQL(GET_CATEGORY_SLUGS);
        return data.categories.nodes.map((cat) => ({
            slug: cat.slug,
        }));
    } catch (error) {
        console.error('Error generating static params for categories:', error);
        return [];
    }
}

export default async function CategoryPage({ params }: { params: Promise<{ slug: string }> }) {
    const { slug } = await params;
    let category: CategoryData['category'] = null;

    try {
        const data = await fetchGraphQL<CategoryData>(GET_CATEGORY_POSTS, { slug });
        category = data.category;
    } catch (error) {
        console.error('Error fetching category posts:', error);
    }

    if (!category) {
        return (
            <div className="bg-slate-50 min-h-screen pt-32 pb-20">
                <div className="container text-center py-32 bg-white rounded-[3rem] border border-dashed border-slate-200">
                    <p className="text-slate-400 text-xl font-light">Category not found.</p>
                    <Link href="/blog" className="text-primary font-bold mt-4 inline-block hover:underline">
                        Back to Blog
                    </Link>
                </div>
            </div>
        );
    }

    const posts = category.posts.nodes;

    return (
        <div className="bg-slate-50 min-h-screen pt-32 pb-20">
            <div className="container">
                {/* Header */}
                <div className="text-center max-w-2xl mx-auto mb-16">
                    <div className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-primary/10 text-primary font-bold text-xs mb-8 uppercase tracking-widest">
                        <Hash className="w-3 h-3" /> Category
                    </div>
                    <h1 className="text-4xl md:text-6xl font-bold text-slate-900 mb-6 font-ubuntu">
                        Posts in <span className="text-gradient">{category.name}</span>
                    </h1>
                    {category.description && (
                        <p className="text-lg text-slate-500 font-light">
                            {category.description}
                        </p>
                    )}
                </div>

                {/* Grid Section */}
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
                    {posts.map((post) => (
                        <article key={post.id} className="card-premium !p-0 flex flex-col group h-full">
                            {post.featuredImage && (
                                <Link href={`/blog/${post.slug}`} className="relative h-64 overflow-hidden rounded-t-[2rem]">
                                    <img
                                        src={post.featuredImage.node.sourceUrl}
                                        alt={post.title}
                                        className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-700"
                                    />
                                    <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent" />
                                </Link>
                            )}
                            <div className="p-8 flex flex-col flex-1">
                                <div className="flex items-center gap-4 mb-4 text-[10px] font-bold text-slate-400 uppercase tracking-widest">
                                    <span className="text-primary">{post.categories.nodes[0]?.name}</span>
                                    <span>•</span>
                                    <div className="flex items-center gap-1">
                                        <Calendar className="w-3 h-3" />
